import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminUploadEvent from "./AdminUploadEvent";

const AdminDashboard = () => {
  const [events, setEvents] = useState([]);
  const [editEventId, setEditEventId] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    date: "",
    time: "",
    venue: "",
    seats: "",
    ticketPrice: "",
  });

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/events");
        setEvents(response.data);
      } catch (error) {
        console.error("Error fetching events", error);
      }
    };
    fetchEvents();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/events/delete/${id}`);
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error("Error deleting event", error);
    }
  };

  const handleEdit = (event) => {
    setEditEventId(event._id);
    setFormData({
      title: event.title,
      description: event.description,
      date: event.date,
      time: event.time,
      venue: event.venue,
      seats: event.seats,
      ticketPrice: event.ticketPrice,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(
        `http://localhost:5001/api/events/update/${editEventId}`,
        formData
      );
      setEvents(
        events.map((event) =>
          event._id === editEventId ? { ...event, ...formData } : event
        )
      );
      setEditEventId(null);
      setFormData({
        title: "",
        description: "",
        date: "",
        time: "",
        venue: "",
        seats: "",
        ticketPrice: "",
      });
    } catch (error) {
      console.error("Error updating event", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleEventAdded = (newEvent) => {
    setEvents([...events, newEvent]);
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <AdminUploadEvent onEventAdded={handleEventAdded} />
      <h2>Existing Events</h2>
      <table>
        <thead>
          <tr>
            <th>Event Name</th>
            <th>Category</th>
            <th>Time</th>
            <th>Seats</th>
            <th>Venue</th>
            <th>Ticket Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map((event) => (
            <tr key={event._id}>
              <td>{event.title}</td>
              <td>{event.description}</td>
              <td>{event.date}</td>
              <td>{event.time}</td>
              <td>{event.venue}</td>
              <td>{event.ticketPrice}</td>
              <td>
                <button onClick={() => handleEdit(event)}>Edit</button>
                <button onClick={() => handleDelete(event._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {editEventId && (
        <form onSubmit={handleUpdate}>
          <h3>Edit Event</h3>
          <div>
            <label>Event Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Time</label>
            <input
              type="text"
              name="time"
              value={formData.time}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Seats</label>
            <input
              type="number"
              name="seats"
              value={formData.seats}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Venue</label>
            <input
              type="text"
              name="venue"
              value={formData.venue}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Ticket Price</label>
            <input
              type="number"
              name="ticketPrice"
              value={formData.ticketPrice}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Update Event</button>
        </form>
      )}
    </div>
  );
};

export default AdminDashboard;
