import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css"; // Make sure to create corresponding CSS for styling

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [reports, setReports] = useState([]); // Assume there's an endpoint for reports
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchUsers();
    fetchReports();
    fetchEvents();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5001/api/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users", error);
    }
  };

  const fetchReports = async () => {
    try {
      const response = await axios.get("http://localhost:5001/api/reports");
      setReports(response.data);
    } catch (error) {
      console.error("Error fetching reports", error);
    }
  };

  const fetchEvents = async () => {
    try {
      const response = await axios.get("http://localhost:5001/api/events");
      setEvents(response.data);
    } catch (error) {
      console.error("Error fetching events", error);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Welcome to the Admin Dashboard</h2>
      <div className="dashboard-blocks">
        <div className="dashboard-block">
          <h3>Users</h3>
          <p>{users.length} Users</p>
        </div>
        <div className="dashboard-block">
          <h3>Reports</h3>
          <p>{reports.length} Reports</p>
        </div>
        <div className="dashboard-block">
          <h3>Events</h3>
          <p>{events.length} Events</p>
        </div>
      </div>
      <div className="dashboard-tables">
        <h3>Users</h3>
        <table className="dashboard-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <h3>Events</h3>
        <table className="dashboard-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Time</th>
              <th>Venue</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event._id}>
                <td>{event.eventName}</td>
                <td>{event.eventCategory}</td>
                <td>{event.time}</td>
                <td>{event.venue}</td>
                <td>${event.ticketPrice}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

